@Override
public Entry<K, V> pollFirstEntry() {
    while(true){
        Entry<K, V> e = firstEntry();
        if(e==null || remove(e.getKey(),e.getValue())){
            return e;
        }
    }
}

@Override
public Entry<K, V> pollLastEntry() {
    while(true){
        Entry<K, V> e = lastEntry();
        if(e==null || remove(e.getKey(),e.getValue())){
            return e;
        }
    }
}
